declare const _default: "\nquery FriendsQuery($displayNames: Boolean!) {\n  Friends {\n    summary(displayNames: $displayNames) {\n      friends {\n        alias\n        note\n        favorite\n        ...friendFields\n      }\n      incoming {\n        ...friendFields\n      }\n      outgoing {\n        ...friendFields\n      }\n      blocklist {\n        ...friendFields\n      }\n    }\n  }\n}\n\nfragment friendFields on Friend {\n  accountId\n  displayName\n  account {\n    externalAuths {\n      type\n      accountId\n      externalAuthId\n      externalDisplayName\n    }\n  }\n}\n";
export default _default;
